// Mobile Navigation
$('.mobile-toggle').click(function() {
    if ($('.topHeader').hasClass('open-nav')) {
        $('.topHeader').removeClass('open-nav');
    } else {
        $('.topHeader').addClass('open-nav');
    }
});

$('.topHeader li a').click(function() {
    if ($('.topHeader').hasClass('open-nav')) {
        $('.navigation').removeClass('open-nav');
        $('.topHeader').removeClass('open-nav');
    }
});
